mapper.select:
select id, member_level_id, username, password, nickname, phone, status, create_time, icon, gender, birthday, city,
job, personalized_signature, source_type, integration, growth, luckey_count, history_integration, avatar,
weixin_openid, invitecode, blance, school_id from ums_member where weixin_openid= #{openId}

select id, member_level_id, username, password, nickname, phone, status, create_time, icon, gender, birthday, city,
job, personalized_signature, source_type, integration, growth, luckey_count, history_integration, avatar,
weixin_openid, invitecode, blance, school_id from ums_member where phone= #{phone}

select id, member_level_id, username, password, nickname, phone, status, create_time, icon, gender, birthday, city, job, personalized_signature, source_type, integration, growth, luckey_count, history_integration, avatar, weixin_openid, invitecode, blance,store_id,room_nums,is_anchor,like_num,forward_num,comment_num,anchor_popularity_value,anchor_fans_num from ums_member where is_anchor = 1
and (username like concat(concat("%",#{record.username}),"%") or nickname like concat(concat("%",#{record.username}),"%") or phone like concat(concat("%",#{record.username}),"%"))
and gender = #{record.gender}
